import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

function ServiceWidget() {
	const [services, setServices] = React.useState(appsmith.model.Services || []);
	const jwtToken = appsmith.model.JWTToken;

	React.useEffect(() => {
		// Listen for changes to the Services model
		appsmith.onModelChange((model) => {
			if (model.Services && JSON.stringify(model.Services) !== JSON.stringify(services)) {
				setServices(model.Services);
			}
		});
	}, [services]);

	const handleToggle = async (service) => {
		try {
			const response = await fetch('http://nestops.duckdns.org:8765/api/toggle', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
					'Authorization': `Bearer ${jwtToken}`,
				},
				body: JSON.stringify({ serviceId: service.id }),
			});

			if (!response.ok) throw new Error('Network response was not ok');
			const result = await response.json();

			// Update the service status based on the result
			setServices(prevServices => prevServices.map(s => 
																									 s.id === service.id ? { ...s, status: result.newStatus } : s));

		} catch (error) {
			console.error('Error toggling service:', error);
		}
	};

	return (
		<div className="widget-container">
		{services.map(service => (
		<div key={service.id} className="service-row">
		<div className="service-name">{service.name}</div>
<div className="service-status">{service.status}</div>
<button className="toggle-button" onClick={() => handleToggle(service)}>Toggle</button>
</div>
))}
	</div>
);
}

appsmith.onReady(() => {
	ReactDOM.render(<ServiceWidget />, document.getElementById('root'));
});
